///////////////////////////////////////////////////////////////////////////////// CLIPBRD.CPP : Win32 clipboard class//#include "common.h"#include "clipbrd.h"Clipboard::Clipboard(){}Clipboard::~Clipboard(){}/////////////////////////////////////////////////////////////////////////////bool Clipboard::open() const{    return OpenClipboard(NULL) == TRUE;}/////////////////////////////////////////////////////////////////////////////bool Clipboard::close() const{    return CloseClipboard() == TRUE;}/////////////////////////////////////////////////////////////////////////////bool Clipboard::empty() const{    return EmptyClipboard() == TRUE;}/////////////////////////////////////////////////////////////////////////////HANDLE Clipboard::getData(UINT format) const{    return GetClipboardData(format);}/////////////////////////////////////////////////////////////////////////////HWND Clipboard::owner() const{    return GetClipboardOwner();}/////////////////////////////////////////////////////////////////////////////HWND Clipboard::openWindow() const{    return GetOpenClipboardWindow();}