///////////////////////////////////////////////////////////////////////////////// INETUTIL.H : Win32 internet utility classes//#ifndef __INETUTIL_H__#define __INETUTIL_H__/////////////////////////////////////////////////////////////////////////////class Socket{public:	// Construction / Destruction	Socket();	~Socket();	// Interface	bool connect(LPCTSTR host, int port);	size_t read(LPVOID pbuff, unsigned int size);	bool write(LPCTSTR pbuff);	// Implementationprotected:	bool bind(LPCTSTR addr, int port);	bool create();	void close();private:	SOCKET _socket;};/////////////////////////////////////////////////////////////////////////////class WebConnect{public:	// Construction / Destruction	WebConnect();	~WebConnect();		enum method {		get = 0,		post,		head	};	// Interface	bool open(LPCTSTR host, int port = 80);	bool request(WebConnect::method, LPCTSTR URI);	size_t read(LPVOID pbuff, unsigned int size);	tstring readPage();	// Implementationprotected:	tstring getHeader(LPCTSTR name) const;private:	tstring responseHeader;	Socket _socket;	static const int blocksize;};/////////////////////////////////////////////////////////////////////////////#endif // __INETUTIL_H__